import React, { Component } from "react";
import styled from "styled-components";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 3
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};
const Div = styled.div`
display:flex;
  img {
    height: 200px;
  }
  .movie{
    display:flex;
    flex-direction:column;
    width:100px;
    cursor: pointer;
    border:1px solid black
  }
  .movie-info{display:flex;
  flex-direction:column}
  justify-content:center;
`;
export class Home extends Component {
  state = { popularMovie: [] };

  async componentDidMount() {
    await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=c377a4de61abbc33e2fbb5bc071c6537&language=en-US&page=1"
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({ popularMovie: data.results });
      });
  }
  getDetail=(id)=>{
    this.props.setId(id)
    window.location.href='./#info'
  }

  render() {
    return (

           <Carousel responsive={responsive}>
           <div style={{ height: "200px", background: "red" }}>Item 1</div>
        <div style={{ height: "200px", background: "red" }}>Item 1</div>
        <div style={{ height: "200px", background: "red" }}>Item 1</div>
        <div style={{ height: "200px", background: "red" }}>Item 1</div>
        <div style={{ height: "200px", background: "red" }}>Item 1</div>
        <div>Item 2</div>
        <div>Item 3</div>
        <div>Item 4</div>
           </Carousel>

    );
  }
}

export default Home;
