import React, { Component } from "react";
import styled from "styled-components";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import { Image } from "semantic-ui-react";

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 7
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 6
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 3
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};
const Div = styled.div`
  width:90%;
  margin:0 auto;
  max-height:50vh;
  min-height:50vh;
  overflow:none;
  .movie{
    display:flex;
    flex-direction:column;
    width:250px;
    cursor: pointer;
    border:1px solid black
    img {
   max-height: 200px;
   min-height: 200px;

  }
  }
  .movie-info{display:flex;
  flex-direction:column;
  align-items:flex-start;}
  h3{
    border:1px solid black;
  }
`;
export class Home extends Component {
  state = { popularMovie: [] };

  async componentDidMount() {
    await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=c377a4de61abbc33e2fbb5bc071c6537&language=en-US&page=1"
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({ popularMovie: data.results });
      });
  }
  getDetail=(id)=>{
    this.props.setId(id)
    window.location.href='./#info'
  }

  render() {
    return (
        <Div>
<h3>Populer Filmler</h3>
          <Carousel responsive={responsive}    additionalTransfrom={0}  ssr 
      partialVisbile
  arrows
  autoPlaySpeed={3000}
  centerMode={false}
  className=""
  containerClass="container-with-dots"
  dotListClass=""
  draggable
  focusOnSelect={false}
  infinite
  itemClass=""
  keyBoardControl
  minimumTouchDrag={80}
  renderButtonGroupOutside={false}
  renderDotsOutside={false}>

        {this.state.popularMovie.map((movie, key) => (
            <div key={movie.id} className= "movie" onClick={()=>this.getDetail(movie.id)}>
            <Image
                        draggable={false}

              src={"https://image.tmdb.org/t/p/w500/" + movie.poster_path}
              style={{ width: "100%", height: "100%" }}

              alt={movie.poster_path}
              />
            <div className="movie-info">
              <h4>{movie.title}</h4>
              <div>
                <p>{movie.release_date}</p>
                <p>{movie.vote_average}</p>
              </div>
            </div>
          </div>
        ))}
        </Carousel>
        </Div>
    );
  }
}

export default Home;
