import React, { Component } from "react";
import styled from "styled-components";
import "react-multi-carousel/lib/styles.css";
import Slider from "./components/Slider";

const Div = styled.div`
  width: 90%;
  margin: 0 auto;
  max-height: 50vh;
  min-height: 50vh;
  overflow: none;
  .movie {
    display: flex;
    flex-direction: column;
    width: 250px;
    cursor: pointer;
    border: 1px solid black img {
      max-height: 200px;
      min-height: 200px;
    }
  }
  .movie-info {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
  }
  h3 {
    border: 1px solid black;
    padding: 10px 15px;
    margin: 10px 0;
    display: inline-block;
    border-radius: 3px;
  }
`;
export class Home extends Component {
  state = { popularMovies: [],
    trendMovies:[] };

  getPopularMovies=async()=>{
    await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=c377a4de61abbc33e2fbb5bc071c6537&language=en-US&page=1"

    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({ popularMovies: data.results });
      });
  }
   getTrendingMovies=async()=>{
    await fetch(
      "https://api.themoviedb.org/3/trending/movie/day?api_key=c377a4de61abbc33e2fbb5bc071c6537&language=en-US&page=1"
    )
      .then((response) => response.json())
      .then((data) => {
        this.setState({ trendMovies: data.results });
      });
  }


  async componentDidMount() {
    await this.getTrendingMovies();
   await this.getPopularMovies();
  }
  getDetail = async (id) => {
    await this.props.setId(id);
    window.location.href = "./#info";
  };

  render() {
    return (
      <Div>
        <h3>Populer Filmler</h3>
        <Slider movies={this.state.popularMovies} />
        <h3>Trend Filmler</h3>
        <Slider movies={this.state.trendMovies} />
      </Div>
    );
  }
}

export default Home;
